import React,{ useState } from "react";
import Router , {useRouter}  from 'next/router';
import { withStyles } from '@material-ui/core/styles';
import PropTypes from 'prop-types';
import TextField from '@material-ui/core/TextField'
import Container from '@material-ui/core/Container'
import Grid from '@material-ui/core/Grid'
import Button from '@material-ui/core/Button';

const styles = theme => ({
        form:{
            backgroundColor:"white",
            maxHeight:"600px",
            position:"fixed",
            top:"50%",
            left:"50%",
            transform:"translate(-50%, -50%)",
            borderRadius:"6px",
            textAlign:"center",
            minWidth:"30%",
        },
        textfield:{
            marginLeft:"50px",
            marginRight:"50px",
            marginBottom:"30px",
            width:"210px"
        },
        title:{
            textAlign:"center",
            fontFamily:"Comic Sans MS, cursive, sans-serif",
            fontSize:"28px",
            color:"#4141af",
            marginBottom:"100px"
        },
        button:{
            marginTop:"20px",
        },
        footer:{
            marginTop:"100px",
            color:"#3520a1"
        }
});

const Join_component = (props) => {
    const { classes } = props;
    const [name, setName] = useState("");
    const [room, setRoom] = useState("");

    return(
        <Container >
            <form style={styles.form}>
            <h2 style={styles.title}>ChatChat</h2>
                <TextField
                id="outlined-password-input"
                label="Pseudonyme"
                type="Pseudonyme"
                autoComplete="current-password"
                variant="outlined"
                className={classes.textfield}
                onChange={e => setName(e.target.value)}
                                />

                <br></br>

                <TextField
                id="outlined-password-input"
                label="Room"
                type="Room"
                autoComplete="current-password"
                variant="outlined"
                className={classes.textfield}
                onChange={e => setRoom(e.target.value)}
                                />

                <br></br>

                <Button
                id=""
                label="Entrer"
                color="primary"
                size="large"
                variant="contained"
                className={classes.button}
                onClick={(e) => (name && room) ? Router.push(`/chat?name=${name}&room=${room}`) : e.preventDefault()}
                >Entrer</Button>


                <p style={styles.footer}>Made with ❤️ by Salim Aouas</p>
            </form>
        </Container>
    )

};

Join_component.propTypes = {
    classes: PropTypes.object.isRequired,
  };

 export default withStyles(styles)(Join_component);
