import Modal from 'react-modal';
import { useState, useEffect } from 'react';
import Container from '@material-ui/core/Container'
import Grid from '@material-ui/core/Grid';

const styles = {
    content : {
        top                   : '50%',
        left                  : '50%',
        right                 : 'auto',
        bottom                : 'auto',
        marginRight           : '-50%',
        transform             : 'translate(-50%, -50%)',
      },
    container:{
    },
    user: {
        height:"30px",
        width:"30px",
        float:"left"
    },
    user_name: {
        float: "left",
        fontFamily: "Comic Sans Ms",
        fontSize: "18px",
        color: "#030311",
        marginTop:"4px",
        marginLeft:"10px"
    }
}
const Users_component = (props) => {
    useEffect(() => {
        setUsersList(props.users);  
    },[props.users]);

    useEffect(()=>{
        if(firstTime && props.open == false)
            firstTime = false;   
        else
            openModal()

    },[props.open])

    const [modalIsOpen,setIsOpen] = useState(false);
    const [usersList, setUsersList] = useState([])
    let firstTime = true;
    let subtitle;

    const openModal = () => {
      setIsOpen(true);
    };

    const afterOpenModal = () => {
       // subtitle.style.color = '#f00';
    };

    const closeModal = () => {
        setIsOpen(false);
    }

    return (
        <div>
            <Modal
            ariaHideApp={false}
            isOpen={modalIsOpen}
            onAfterOpen={afterOpenModal}
            onRequestClose={closeModal}
            style={styles.content}
            contentLabel="Example Modal"
            >
            <Container>
                <Grid container styles={styles.container}>
                    {usersList.map((user, id)=>{
                        return(<Grid item key={id} xs={12}>
                            <img key={`img${id}`} src="/images/user.png" style={styles.user} />
                            <p key={`paragraph${id}`} style={styles.user_name}>{user}</p>
                        </Grid>)
                    })}
                </Grid>
            </Container>
            </Modal>
        </div>)
}


export default Users_component;
