import React,{ useState } from "react"
import Link from 'next/link'
import TextField from '@material-ui/core/TextField'
import Container from '@material-ui/core/Container'
import Grid from '@material-ui/core/Grid'
import Button from '@material-ui/core/Button';


const Join_component = () => {

const styles = {
    form:{
        backgroundColor:"white",
        maxHeight:"600px",
        position:"fixed",
        top:"50%",
        left:"50%",
        transform:"translate(-50%, -50%)",
        borderRadius:"6px",
        textAlign:"center",
        minWidth:"30%",
    },
    textField:{
        marginLeft:"50px",
        marginRight:"50px",
        marginBottom:"30px",
        width:"210px"
    },
    title:{
        textAlign:"center",
        fontFamily:"Comic Sans MS, cursive, sans-serif",
        fontSize:"28px",
        color:"#4141af",
        marginBottom:"100px"
    },
    button:{
        marginTop:"20px",
    },
    footer:{
        marginTop:"100px",
        color:"#3520a1"
    }
}

    const [name, setName] = useState("");
    const [room, setRoom] = useState("");

    return(
        <Container >
            <form style={styles.form}>
            <h2 style={styles.title}>ChatChat</h2>
                <TextField
                id="outlined-password-input"
                label="Pseudonyme"
                type="Pseudonyme"
                autoComplete="current-password"
                variant="outlined"
                style={styles.textField}
                onChange={e => setName(e.target.value)}
                                />

                <br></br>

                <TextField
                id="outlined-password-input"
                label="Room"
                type="Room"
                autoComplete="current-password"
                variant="outlined"
                style={styles.textField}
                onChange={e => setRoom(e.target.value)}
                                />

                <br></br>

                {/* <Link onClick={(e) => (!name || !room) ? e.preventDefault() : null } href={`/chat?name=${name}&room=${room}`}> */}
                    <Button
                    id=""
                    label="Entrer"
                    color="primary"
                    size="large"
                    variant="contained"
                    style={styles.button}
                    >Entrer</Button>
                {/* </Link> */}


                <p style={styles.footer}>Made with ❤️ by Salim Aouas</p>
            </form>
        </Container>
    )

};


 export default Join_component;
