import Container from '@material-ui/core/Container'
import TextField from '@material-ui/core/TextField'
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import { relative } from 'path';
import { useEffect, useState } from 'react';
import { useRouter } from 'next/router'
import io from 'socket.io-client';
import Cookies from 'universal-cookie'
import moment from 'moment';
import ScrollToBottom from 'react-scroll-to-bottom';
import { css } from 'glamor';

const CSS_SCROLL = css({
    height: 400,
    overflow: `auto`
});

const styles = {
    container: {
        backgroundColor: "white",
        height: "570px",
        position: "fixed",
        top: "50%",
        left: "50%",
        transform: "translate(-50%, -50%)",
        borderRadius: "6px",
        textAlign: "center",
        maxWidth: "600px",
        minWidth: "20%",
        padding: "0"
    },
    topbar: {
        position: "relative",
        backgroundColor: "#3f51b5",
        border: "2px solid white",
        borderRadius: "6px",
        height: "60px",
        width: "100%",
    },
    pastille: {
        color: "#a8ffa8",
    },
    pseudo: {
        color: "white",
        position: "absolute",
        top: "0%",
        left: "0%",
        paddingLeft: "30px",
        marginTop: "15px",
        fontSize: "20px"
    },
    close: {
        position: "absolute",
        top: "0%",
        right: "0%",
        height: "15px",
        width: "15px",
        paddingTop: "5px",
        paddingRight: "5px"
    },
    sub_container: {
        position: "absolute",
        padding: "0"
    },
    hr: {
        padding: "0",
    },
    input_container: {
        position: "relative",
        padding: "0",
    },
    input: {
        position: "absolute",
        left: "0%",
        width:"68%",
        paddingLeft: "10px",
        marginRight: "20px",
    },
    button: {
        position: "absolute",
        right: "0%",
        marginRight: "10px",
        height: "76px",
        width:"25%"
    },
    messages: {
        overflowY: "scroll",
        display: "flex",
        flexDirection: "column",
        height: "400px",
        overflowX: "hidden"
    },
    incomingMessage: {
        borderRadius: "8px",
        backgroundColor: "#1982FC",
        minHeight: "20px",
        width: "100px",
        textAlign: "center",
        padding: "20px 5px 20px 5px",
    },
    adminMessage: {
        borderRadius: "8px",
        backgroundColor: "rgb(236, 236, 236)",
        minHeight: "20px",
        width: "100px",
        textAlign: "center",
        padding: "20px 5px 20px 5px",
    },
    myMessage: {
        borderRadius: "8px",
        backgroundColor: "#43CC47",
        minHeight: "20px",
        width: "100px",
        textAlign: "center",
        padding: "20px 5px 20px 5px",
    },
    incomingMessageList: {
        height: "100%",
        marginTop: "5px"
    },
    myMessageList: {
        height: "100%",
        marginRight: "10px"
    },
    pseudoMymessage: {
        position: "absolute",
        marginTop: "20px",
        color: "#a2a2a2",
        fontSize: "15px",
        marginLeft: "-40px"

    },
    pseudoIncomingMessage: {
        position: "absolute",
        marginTop: "-40px",
        color: "#a2a2a2",
        fontSize: "15px",
        marginLeft: "110px"
    },
    itemIncomingMessage: {
        position: "relative",
        marginLeft: "20px",
        marginBottom: "-60px"
    },
    itemMyMessage: {
        position: "relative",
        marginRight: "50px",
        minHeight: "0px",
    },
    containerItemMyMessage: {
        position: "absolute",
        right: "0%"
    }
}

let socket;
const cookies = new Cookies();
moment.locale('fr');

const Chat_component = ({location}) => {
    const router = useRouter();
    const [name, setName] = useState("");
    const [room, setRoom] = useState("");
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState([]);
    const ENDPOINT = "localhost:8080";

    useEffect(() => {
        const { name,room } = router.query;
        socket = io(ENDPOINT);

        if((name != undefined) && (room != undefined)){
            setName(name);
            setRoom(room);

            let tmp_date = new Date(moment().add(2, 'hours').toDate());
            cookies.set('name',name,{expires: tmp_date});
            cookies.set('room',room,{expires: tmp_date});

            let namedStored = cookies.get('name');
            let roomStored = cookies.get('room');

            socket.emit('join', { name: namedStored, room: roomStored }, () => {

            })
        }

        return () => {
            socket.emit('disconnect');
            socket.off();
        };

    }, [ENDPOINT,router]);

    useEffect(()=>{
        socket.on('message', (message) => {
            setMessages([...messages, message]);
        })

    }, [messages]);

    const sendMessage = (e) => {
        event.preventDefault();

        if(message && room != undefined && name != undefined){
            socket.emit('sendMessage', ( {user: name, text: message} ), () => {
                setMessage('');
            });

       }
    }

    return (<div>
        <Container style={styles.container}>
            <Container style={styles.sub_container}>
                <Container style={styles.topbar}>
                    <p style={styles.pseudo}><span style={styles.pastille}>â€¢ </span>{name}</p>
                    <img style={styles.close} src="/images/close.png">
                    </img>
                </Container>

                <Grid container style={styles.messages}>
                <ScrollToBottom id="scrollToBottom" className={ CSS_SCROLL }>

                    <Grid container style={styles.incomingMessageList} direction="column" spacing={8} wrap="nowrap">
                            {messages.map((message, i) => {
                                    if(message.user == name.trim().toLowerCase()){
                                        return( <Grid item key={i} id={`inboxMessage${i}`} style={styles.itemIncomingMessage} xs={12}>
                                            <Grid container key={`${i}2`} style={styles.containerItemIncomingMessage}>
                                    <Grid item key={`${i}3`} style={styles.incomingMessage}>{message.text}</Grid>
                                                <Grid key={`${i}4`} item style={styles.pseudoIncomingMessage}>{message.user}</Grid>
                                            </Grid>
                                        </Grid>)
                                    }
                                    else if (message.user == "admin"){
                                        return( <Grid item key={i} id={`inboxMessage${i}`} style={styles.itemIncomingMessage} xs={12}>
                                            <Grid container key={`${i}2`} style={styles.containerItemIncomingMessage}>
                                    <Grid item key={`${i}3`} style={styles.adminMessage}>{message.text}</Grid>
                                                <Grid key={`${i}4`} item style={styles.pseudoIncomingMessage}>Admin</Grid>
                                            </Grid>
                                        </Grid>)
                                    }
                                    else{
                                        return (<Grid item  key={i} id={`myMessage${i}`} style={styles.itemMyMessage} xs={12}>
                                            <Grid item container key={`${i}2`} style={styles.containerItemMyMessage}>
                                                <Grid item key={`${i}3`} style={styles.pseudoMymessage}>{message.user}</Grid>
                                                <Grid item key={`${i}4`} style={styles.myMessage}>{message.text}</Grid>
                                            </Grid>
                                        </Grid>)
                                    }
                                })}
                    </Grid>

                    </ScrollToBottom>

                </Grid>

                <Grid container style={styles.input_container} fluid="true"  diction="row" >
                    <hr style={styles.hr}></hr>
                    <Grid item xs={12}>
                        <TextField
                            id="outlined-multiline-static"
                            label="Message"
                            multiline
                            rows={2}
                            variant="filled"
                            style={styles.input}
                            onChange={(e) => setMessage(e.target.value)}
                            value={message}
                            onKeyPress={(e) => (e.key === "Enter") ? sendMessage(e) : null }
                        />
                    </Grid>

                    <Grid item xs={6}>
                        <Button
                            id=""
                            label="Entrer"
                            color="primary"
                            size="large"
                            variant="contained"
                            style={styles.button}
                        >Envoyer</Button>
                    </Grid>


                </Grid>

            </Container>
        </Container>
    </div>)
};

export default Chat_component;
