import React,{ useState } from "react";
import Router from 'next/router';
import Container from '@material-ui/core/Container'
import Button from '@material-ui/core/Button';

const styles = {
        form:{
            backgroundColor:"white",
            maxHeight:"600px",
            position:"fixed",
            top:"50%",
            left:"50%",
            transform:"translate(-50%, -50%)",
            borderRadius:"6px",
            textAlign:"center",
            minWidth:"30%",
        },
        textField:{
            marginLeft:"50px",
            marginRight:"50px",
            marginBottom:"30px",
            width:"210px",
            height:"40px",
            borderRadius: "10px",
            padding:"10px",
            boxShadow:"4px 4px 10px" 
        },
        title:{
            textAlign:"center",
            fontFamily:"Comic Sans MS, cursive, sans-serif",
            fontSize:"28px",
            color:"#4141af",
            marginBottom:"100px"
        },
        button:{
            marginTop:"20px",
        },
        footer:{
            marginTop:"100px",
            color:"#3520a1"
        }
}

const Join_component = () => {
    const [name, setName] = useState("");
    const [room, setRoom] = useState("");

    return(<div>
        <Container >
            <form style={styles.form}>
            <h2 style={styles.title}>ChatChat</h2>
                <input
                id="outlined-password-input"
                label="Pseudonyme"
                type="Pseudonyme"
                autoComplete="current-password"
                variant="outlined"
                style={styles.textField}
                onChange={e => setName(e.target.value)}
                                />

                <br></br>

                <input
                id="outlined-password-input"
                label="Room"
                type="Room"
                autoComplete="current-password"
                variant="outlined"
                style={styles.textField}
                onChange={e => setRoom(e.target.value)}
                                />

                <br></br>

                <Button
                id=""
                label="Entrer"
                color="primary"
                size="large"
                variant="contained"
                style={styles.button}
                onClick={(e) => (name && room) ? Router.push(`/chat?name=${name}&room=${room}`) : e.preventDefault()}
                >Entrer</Button>


                <p style={styles.footer}>Made with ❤️ by Salim Aouas</p>
            </form>
        </Container></div>
    )

};


 export default Join_component;
